package com.yeepay.yop.sdk;

import com.yeepay.yop.sdk.exception.YopClientException;
import com.yeepay.yop.sdk.service.kj.KjClient;
import com.yeepay.yop.sdk.service.kj.KjClientBuilder;
import com.yeepay.yop.sdk.service.kj.request.*;
import com.yeepay.yop.sdk.service.kj.response.*;
import com.yeepay.yop.sdk.service.kj.model.OnlinePayOrderRequestDTO;
import com.yeepay.yop.sdk.service.kj.model.OnlinePayOrderResponseDTO;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.List;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class Example {

    private static final Logger LOGGER = LoggerFactory.getLogger(Example.class);

    // 该Client线程安全，请使用单例模式，多次请求共用
    private static final KjClient api = KjClientBuilder.builder().build();

    public static void main(String[] args) {
        OnlinepayOrderRequest request = new OnlinepayOrderRequest();
        request.setBody(new OnlinePayOrderRequestDTO());
        try {
            OnlinepayOrderResponse response = api.onlinepayOrder(request);
            LOGGER.info("result:{}", response.getResult());
        } catch (YopClientException e) {
            LOGGER.error("Exception when calling KjClient#onlinepayOrder, ex:", e);
        }
    }

}

